
public class IfLadder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch;
		ch= 'A';
		if(ch== 'A')
		                 System.out.println("Vowel");
		else if(ch== 'E')
		                 System.out.println("Vowel");
		else if(ch== 'I')
		                 System.out.println("Vowel");
		else if(ch== 'O')
		                 System.out.println("Vowel");
		else if(ch== 'U')
		                 System.out.println("Vowel");
		else
		                 System.out.println("Consonant");

	}

}
